import React, { useState, useEffect } from "react"
import { Link, useParams } from "react-router-dom"

const SingleDog = () => {
    const [dog, setDog] = useState([]);
    const { name } = useParams();
    useEffect(() => {
        fetch(`https://api.thedogapi.com/v1/breeds/search?q=${name}`)
            .then(response => response.json())
            .then(data => {
                setDog(data);
                console.log(data);
            });
    }, [name]);
    return (
        <>
            {/* <h1>I am {name}</h1> */}
            <section className="conatiner">
                {dog.map((item) => (
                    <div key={item.id}>
                        <div>
                            <img
                                src={`https://cdn2.thedogapi.com/images/${item.reference_image_id}.jpg`}
                                alt={item.name}
                            />
                        </div>
                        <div>
                            <h1>
                                {item.name}
                            </h1>
                            {item.description && (
                                <p className>
                                    {item.description}
                                </p>
                            )}

                            <ul>
                                <li>
                                    <span>Bred For:</span>{" "}
                                    {item.bred_for}
                                </li>
                                <li>
                                    <span>Height:</span>{" "}
                                    {item.height.metric} cm
                                </li>
                                <li>
                                    <span>Weight:</span>{" "}
                                    {item.weight.metric} kgs
                                </li>
                                <li>
                                    <span>Breed Group:</span>{" "}
                                    {item.breed_group}
                                </li>
                                <li>
                                    <span>Lifespan:</span>{" "}
                                    {item.life_span}
                                </li>
                                <li>
                                    <span>Temperament:</span>{" "}
                                    {item.temperament}
                                </li>
                            </ul>

                            <Link to="/"> &larr; Back</Link>
                        </div>
                    </div>
                ))}
            </section>
        </>
    )
}

export default SingleDog;
