import { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMapLocationDot } from '@fortawesome/free-solid-svg-icons';

document.addEventListener('DOMContentLoaded', function() {
    // Add your code here
    const map = document.getElementById('map');

map.addEventListener('mouseenter', () => {
    // Create a new element to hold the text
    const textElement = document.createElement('div');
    textElement.textContent = 'Click on the pins to see images!';
    textElement.classList.add('map-text');
  
    // Append the new element to the map element
    map.appendChild(textElement);
  
    // After 2 seconds, remove the new element
    
  });
  });

function Icon() {
    const handleScroll = () => {
      const el = document.querySelector('.map');
      el.scrollIntoView({ behavior: 'smooth' });
    }
  
    return (
      <span>
        <FontAwesomeIcon icon={faMapLocationDot} className="my-icon" onClick={handleScroll} title="Want to know about dogs breed in different countries? Click Me! " />
      </span>
    );
  }

export default Icon;
